import Card08, { type Card08Props } from "./card/card-08";

interface CardGrid {
    items: Card08Props[];
    gridTitle?: string;
}

const collections: Card08Props[] = [
    {
        title: "Summer Dress Collection",
        subtitle: "Fit with the latest trends",
        image: "https://assets.lummi.ai/assets/QmVQpR9kUDEiFupgWrzaUYVii2TLa5zE7h9XDU7okBFdyc?auto=format&w=450",
        badge: {
            text: "New",
            variant: "orange",
        },
    },
    {
        image: "https://assets.lummi.ai/assets/QmXFtkNH6GJy581VwChHZeQF6qfQM9bkLhF2kDh1px5qKB?auto=format&w=1500",
        title: "Autumn Essentials",
        subtitle: "Warm and cozy for the colder months",
        badge: {
            text: "New",
            variant: "orange",
        },
    },
    {
        image: "https://www.lummi.ai/api/pro/image/a06ede0d-63fb-4d99-be4c-9ba0840cf779?asset=original&cb=1nK52A&auto=format&w=1500",
        title: "Winter Collection",
        subtitle: "Colorful for the festive season",
        badge: {
            text: "New",
            variant: "orange",
        },
    },
    {
        image: "https://assets.lummi.ai/assets/QmPdy3qFqQF8gDQ6KuuUrRaqez3cbdeaYw3GMrxBynLfGd?auto=format&w=1500",
        title: "Spring Accessories",
        subtitle: "Hidden treasures for the spring season",
        badge: {
            text: "New",
            variant: "orange",
        },
    },
    {
        image: "https://assets.lummi.ai/assets/QmVQpR9kUDEiFupgWrzaUYVii2TLa5zE7h9XDU7okBFdyc?auto=format&w=450",
        title: "Holiday Special",
        subtitle: "Simple.",
        badge: {
            text: "New",
            variant: "orange",
        },
    },
];

export default function CardGrid({
    items = collections,
    gridTitle = "Collections 2025 - Summer",
}: CardGrid) {
    if (!items || items.length === 0) {
        return (
            <div className="flex items-center justify-center h-64">
                <p className="text-lg text-zinc-500 dark:text-zinc-400">
                    No items to display.
                </p>
            </div>
        );
    }

    return (
        <section className="w-full py-12 md:py-16 lg:py-20">
            <div className="container mx-auto px-4 md:px-6">
                {gridTitle && (
                    <h2 className="mb-2 tracking-tighter text-3xl font-bold  text-left text-zinc-900 dark:text-zinc-100 sm:text-4xl">
                        {gridTitle}
                    </h2>
                )}
                <p className="mb-8 text-lg text-left text-zinc-900 dark:text-zinc-100 tracking-tight">
                    Explore the latest collections for the summer season.
                </p>
                <div className="grid grid-cols-1 gap-4 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 place-items-center">
                    {items.map((item, index) => (
                        <Card08
                            key={`${item.href}-${item.title}-${index}`}
                            title={item.title}
                            subtitle={item.subtitle}
                            image={item.image}
                            badge={item.badge}
                            href={item.href}
                        />
                    ))}
                </div>
            </div>
        </section>
    );
}

import { cn } from "@/lib/utils";
import { ArrowUpRight } from "lucide-react";
import Image from "next/image";
import Link from "next/link";

export interface Card08Props {
    title?: string;
    subtitle?: string;
    image?: string;
    badge?: {
        text: string;
        variant: "pink" | "indigo" | "orange";
    };
    href?: string;
}

export default function Card08({
    title = "Modern Design Systems",
    subtitle = "Explore the fundamentals of contemporary UI design",
    image = "https://ferf1mheo22r9ira.public.blob.vercel-storage.com/profile-mjss82WnWBRO86MHHGxvJ2TVZuyrDv.jpeg",
    badge = { text: "New", variant: "orange" },
    href = "#",
}: Card08Props) {
    return (
        <Link href={href} className="block w-full max-w-[280px] group">
            <div
                className={cn(
                    "relative overflow-hidden rounded-2xl",
                    "bg-white/80 dark:bg-zinc-900/80",
                    "backdrop-blur-xl",
                    "border border-zinc-200/50 dark:border-zinc-800/50",
                    "shadow-xs",
                    "transition-all duration-300",
                    "hover:shadow-md",
                    "hover:border-zinc-300/50 dark:hover:border-zinc-700/50"
                )}
            >
                <div className="relative h-[320px] overflow-hidden">
                    <Image
                        src={image}
                        alt={title}
                        fill
                        sizes="(max-width: 768px) 100vw, (max-width: 1200px) 50vw, 33vw"
                        className="object-cover"
                    />
                </div>

                <div
                    className={cn(
                        "absolute inset-0",
                        "bg-linear-to-t from-black/90 via-black/40 to-transparent"
                    )}
                />

                <div className="absolute top-3 right-3">
                    <span
                        className={cn(
                            "px-2.5 py-1 rounded-lg text-xs font-medium",
                            "bg-white/90 text-zinc-800",
                            "dark:bg-zinc-900/90 dark:text-zinc-200",
                            "backdrop-blur-md",
                            "shadow-xs",
                            "border border-white/20 dark:border-zinc-800/50"
                        )}
                    >
                        {badge.text}
                    </span>
                </div>

                <div className="absolute bottom-0 left-0 right-0 p-5">
                    <div className="flex items-center justify-between gap-3">
                        <div className="space-y-1.5">
                            <h3 className="text-lg font-semibold text-white dark:text-zinc-100 leading-snug tracking-tighter">
                                {title}
                            </h3>
                            <p className="text-sm text-zinc-200 dark:text-zinc-300 line-clamp-2 tracking-tight">
                                {subtitle}
                            </p>
                        </div>
                        <div
                            className={cn(
                                "p-2 rounded-full",
                                "bg-white/10 dark:bg-zinc-800/50",
                                "backdrop-blur-md",
                                "group-hover:bg-white/20 dark:group-hover:bg-zinc-700/50",
                                "transition-colors duration-300 group"
                            )}
                        >
                            <ArrowUpRight className="w-4 h-4 text-white group-hover:-rotate-12 transition-transform duration-300" />
                        </div>
                    </div>
                </div>
            </div>
        </Link>
    );
}
