                    <Button
                        variant="default"
                        size="sm"
                        className={cn(
                            "flex items-center justify-center gap-2",
                            "text-xs font-medium",
                            "bg-zinc-900 dark:bg-zinc-50 text-zinc-50 dark:text-zinc-900",
                            "hover:bg-zinc-800 dark:hover:bg-zinc-200"
                        )}
                    >
                        <ArrowRight className="w-3.5 h-3.5" />
                        <span>More</span>
                    </Button>
                </div>
            </CardFooter>
        </Card>
    );
}

